import React, { useState } from 'react';
import Navbar from './Navbar';
import Content from './Content';
import { BrowserRouter as Router, Route, Switch, useLocation } from "react-router-dom";

export default function Page2() {
     const articles = [
        {
          "img" : "https://talksport.com/wp-content/uploads/sites/5/2023/06/LB-tsport-freddie-copy.jpg?strip=all&quality=100&w=1500&h=1000&crop=1",
          "url" : "/videos/vid1.mp4",
          "no" : 1
        },
        {
          "img" : "https://talksport.com/wp-content/uploads/sites/5/2023/06/LB-tsport-freddie-copy.jpg?strip=all&quality=100&w=1500&h=1000&crop=1",
          "url" : "/videos/vid1.mp4",
          "no" : 2
        },
        {
          "img" : "https://talksport.com/wp-content/uploads/sites/5/2023/06/LB-tsport-freddie-copy.jpg?strip=all&quality=100&w=1500&h=1000&crop=1",
          "url" : "/videos/vid1.mp4",
          "no" : 3
        },
        {
          "img" : "https://talksport.com/wp-content/uploads/sites/5/2023/06/LB-tsport-freddie-copy.jpg?strip=all&quality=100&w=1500&h=1000&crop=1",
          "url" : "/videos/vid1.mp4",
          "no" : 4
        },{
          "img" : "https://talksport.com/wp-content/uploads/sites/5/2023/06/LB-tsport-freddie-copy.jpg?strip=all&quality=100&w=1500&h=1000&crop=1",
          "url" : "/videos/vid1.mp4",
          "no" : 5
        },{
          "img" : "https://talksport.com/wp-content/uploads/sites/5/2023/06/LB-tsport-freddie-copy.jpg?strip=all&quality=100&w=1500&h=1000&crop=1",
          "url" : "/videos/vid1.mp4",
          "no" : 6
        },{
          "img" : "https://talksport.com/wp-content/uploads/sites/5/2023/06/LB-tsport-freddie-copy.jpg?strip=all&quality=100&w=1500&h=1000&crop=1",
          "url" : "/videos/vid1.mp4",
          "no" : 7
        },{
          "img" : "https://talksport.com/wp-content/uploads/sites/5/2023/06/LB-tsport-freddie-copy.jpg?strip=all&quality=100&w=1500&h=1000&crop=1",
          "url" : "/videos/vid1.mp4",
          "no" : 8
        },{
          "img" : "https://talksport.com/wp-content/uploads/sites/5/2023/06/LB-tsport-freddie-copy.jpg?strip=all&quality=100&w=1500&h=1000&crop=1",
          "url" : "/videos/vid1.mp4",
          "no" : 9
        },{
          "img" : "https://talksport.com/wp-content/uploads/sites/5/2023/06/LB-tsport-freddie-copy.jpg?strip=all&quality=100&w=1500&h=1000&crop=1",
          "url" : "/videos/vid1.mp4",
          "no" : 10
        }
    ];
    const articlesPerPage = 5;
  const totalPages = Math.ceil(articles.length / articlesPerPage);

  const [currentPage, setCurrentPage] = useState(1);

  const handlePrevious = () => {
    setCurrentPage((prevPage) => prevPage - 1);
  };

  const handleNext = () => {
    setCurrentPage((prevPage) => prevPage + 1);
  };

  const startIndex = (currentPage - 1) * articlesPerPage;
  const endIndex = startIndex + articlesPerPage;
  const slicedArticles = articles.slice(startIndex, endIndex);
  const VideoPage = () => {
          const location = useLocation();
          const { movieurl } = location.state;
          return (
            <div>
              <div className="video-container">
                <video controls className="responsive-video">
                  <source src={movieurl} type="video/mp4" />
                  Your browser does not support the video tag.
                </video>
              </div>
            </div>
          );
        };
  return (
    <div>
      <Navbar />
      <br />
      <div className="container">
        <div className="container d-flex justify-content-between">
          <button className="btn btn-dark" onClick={handlePrevious} disabled={currentPage === 1}>
            Previous
          </button>
          <button className="btn btn-dark" onClick={handleNext} disabled={currentPage === totalPages}>
            Next
          </button>
        </div>
        <div className="row">
          <Router>
            <Switch>
              <Route path="/video-page" component={VideoPage} />
              <Route exact path="/">
                {slicedArticles.map((element, index) => (
                  <div className="col-md-4" key={element.no}>
                    <Content imageurl={element.img} movieurl={element.url} />
                  </div>
                ))}
              </Route>
            </Switch>
          </Router>
        </div>
        <div className="container d-flex justify-content-between">
          <button className="btn btn-dark" onClick={handlePrevious} disabled={currentPage === 1}>
            Previous
          </button>
          <button className="btn btn-dark" onClick={handleNext} disabled={currentPage === totalPages}>
            Next
          </button>
        </div>
      </div>
    </div>
  );
}
