import React from "react";
import { Link } from "react-router-dom";
import { MDBIcon } from "mdb-react-ui-kit";

export default function Content(props) {
  const { imageurl, movieurl } = props;

  const handleClick = () => {
    // You can perform any necessary actions before navigating to the video page
    // For example, you can store the selected video URL in localStorage or state before redirecting
    // Here, we'll assume you're using React Router for navigation
    // Replace 'video-page' with the actual path to your video page component
    window.location.href = "/video-page";
    // Alternatively, you can use React Router's history object to navigate and pass the movieurl as state
    // history.push({ pathname: "/video-page", state: { movieurl } });
  };

  return (
    <div>
      <div className="container">
        <div className="row">
          <div className="items col-md">
            <div className="bg-image">
              <img src={imageurl} className="img-fluid" alt="Sample" />
              <div className="mask">
                <div className="d-flex justify-content-center align-items-center h-100">
                  <p className="contenticon text-white mb-0" onClick={handleClick}>
                    <MDBIcon far icon="play-circle" />
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}




