import React from 'react';
import Navbar from './Navbar';
import Content from './Content';
import { BrowserRouter as Router, Route, Switch, useLocation } from "react-router-dom";

export default function Page2() {
  const articlesPerPage = 6;
  const articles = [
    {
      "img" : "https://talksport.com/wp-content/uploads/sites/5/2023/06/LB-tsport-freddie-copy.jpg?strip=all&quality=100&w=1500&h=1000&crop=1",
      "url" : "/videos/vid1.mp4",
      "no" : 1
    },
    {
      "img" : "https://talksport.com/wp-content/uploads/sites/5/2023/06/LB-tsport-freddie-copy.jpg?strip=all&quality=100&w=1500&h=1000&crop=1",
      "url" : "/videos/vid1.mp4",
      "no" : 2
    },
    {
      "img" : "https://talksport.com/wp-content/uploads/sites/5/2023/06/LB-tsport-freddie-copy.jpg?strip=all&quality=100&w=1500&h=1000&crop=1",
      "url" : "/videos/vid1.mp4",
      "no" : 3
    },
    {
      "img" : "https://talksport.com/wp-content/uploads/sites/5/2023/06/LB-tsport-freddie-copy.jpg?strip=all&quality=100&w=1500&h=1000&crop=1",
      "url" : "/videos/vid1.mp4",
      "no" : 4
    },{
      "img" : "https://talksport.com/wp-content/uploads/sites/5/2023/06/LB-tsport-freddie-copy.jpg?strip=all&quality=100&w=1500&h=1000&crop=1",
      "url" : "/videos/vid1.mp4",
      "no" : 5
    },{
      "img" : "https://talksport.com/wp-content/uploads/sites/5/2023/06/LB-tsport-freddie-copy.jpg?strip=all&quality=100&w=1500&h=1000&crop=1",
      "url" : "/videos/vid1.mp4",
      "no" : 6
    },{
      "img" : "https://www.google.com/imgres?imgurl=https%3A%2F%2Fmedia.sproutsocial.com%2Fuploads%2F2017%2F02%2F10x-featured-social-media-image-size.png&tbnid=-VCM1w56w6u5VM&vet=12ahUKEwjGrpmW8ur_AhVPzKACHe7sDLkQMygMegUIARDbAQ..i&imgrefurl=https%3A%2F%2Fsproutsocial.com%2Finsights%2Fsocial-media-image-sizes-guide%2F&docid=aVwfeogQqK1XmM&w=780&h=460&q=image&ved=2ahUKEwjGrpmW8ur_AhVPzKACHe7sDLkQMygMegUIARDbAQ",
      "url" : "/videos/vid1.mp4",
      "no" : 7
    },{
      "img" : "https://talksport.com/wp-content/uploads/sites/5/2023/06/LB-tsport-freddie-copy.jpg?strip=all&quality=100&w=1500&h=1000&crop=1",
      "url" : "/videos/vid1.mp4",
      "no" : 8
    },{
      "img" : "https://talksport.com/wp-content/uploads/sites/5/2023/06/LB-tsport-freddie-copy.jpg?strip=all&quality=100&w=1500&h=1000&crop=1",
      "url" : "/videos/vid1.mp4",
      "no" : 9
    },{
      "img" : "https://talksport.com/wp-content/uploads/sites/5/2023/06/LB-tsport-freddie-copy.jpg?strip=all&quality=100&w=1500&h=1000&crop=1",
      "url" : "/videos/vid1.mp4",
      "no" : 10
    }
];

    const VideoPage = () => {
      const location = useLocation();
      const { movieurl } = location.state;
      return (
        <div>
          <div className="video-container">
            <video controls className="responsive-video">
              <source src={movieurl} type="video/mp4" />
              Your browser does not support the video tag.
            </video>
          </div>
        </div>
      );
    };
    const [currentPage, setCurrentPage] = useState(1);

    const handleNextPage = () => {
      setCurrentPage((prevPage) => prevPage + 1);
    };
  
    const handlePreviousPage = () => {
      setCurrentPage((prevPage) => prevPage - 1);
    };


  return (
    <div>
      <Navbar />
      <br />
      <div className="container">
      <div className="container d-flex justify-content-between">
          <button className='btn btn-dark' onClick={handlePreviousPage} >Previous</button>
          <button className='btn btn-dark' onClick={handleNextPage} >Next</button>
        </div>
        <div className="row">
          <Router>
            <Switch>
              <Route path="/video-page" component={VideoPage} />
              
              <Route path="/">
                
                {articles.slice(0, articlesPerPage)
                .map((element) => (
                  
                  <div className="col-md-4" key={element.no}>
                    <Content imageurl={element.img} movieurl={element.url} />
                  </div>
                ))}
              </Route>
            </Switch>
          </Router>
        </div>
      </div>
    </div>
  );
}
