import React, { useState } from 'react';
import Navbar from './Navbar';
import Content from './Content';
import { BrowserRouter as Router, Route, Switch, useLocation, NavLink } from "react-router-dom";

export default function Page2() {
  const articlesPerPage = 6;
    const articles = [
    {
      "img" : "https://talksport.com/wp-content/uploads/sites/5/2023/06/LB-tsport-freddie-copy.jpg?strip=all&quality=100&w=1500&h=1000&crop=1",
      "url" : "/videos/vid1.mp4",
      "no" : 1
    },
    {
      "img" : "https://talksport.com/wp-content/uploads/sites/5/2023/06/LB-tsport-freddie-copy.jpg?strip=all&quality=100&w=1500&h=1000&crop=1",
      "url" : "/videos/vid1.mp4",
      "no" : 2
    },
    {
      "img" : "https://talksport.com/wp-content/uploads/sites/5/2023/06/LB-tsport-freddie-copy.jpg?strip=all&quality=100&w=1500&h=1000&crop=1",
      "url" : "/videos/vid1.mp4",
      "no" : 3
    },
    {
      "img" : "https://talksport.com/wp-content/uploads/sites/5/2023/06/LB-tsport-freddie-copy.jpg?strip=all&quality=100&w=1500&h=1000&crop=1",
      "url" : "/videos/vid1.mp4",
      "no" : 4
    },{
      "img" : "https://talksport.com/wp-content/uploads/sites/5/2023/06/LB-tsport-freddie-copy.jpg?strip=all&quality=100&w=1500&h=1000&crop=1",
      "url" : "/videos/vid1.mp4",
      "no" : 5
    },{
      "img" : "https://talksport.com/wp-content/uploads/sites/5/2023/06/LB-tsport-freddie-copy.jpg?strip=all&quality=100&w=1500&h=1000&crop=1",
      "url" : "/videos/vid1.mp4",
      "no" : 6
    },{
      "img" : "https://ichef.bbci.co.uk/live-experience/cps/624/cpsprodpb/17D0E/production/_130105579_ashespreviewv2.jpg",
      "url" : "/videos/vid1.mp4",
      "no" : 7
    },{
      "img" : "https://ichef.bbci.co.uk/live-experience/cps/624/cpsprodpb/17D0E/production/_130105579_ashespreviewv2.jpg",
      "url" : "/videos/vid1.mp4",
      "no" : 8
    },{
      "img" : "https://talksport.com/wp-content/uploads/sites/5/2023/06/LB-tsport-freddie-copy.jpg?strip=all&quality=100&w=1500&h=1000&crop=1",
      "url" : "/videos/vid1.mp4",
      "no" : 9
    },{
      "img" : "https://talksport.com/wp-content/uploads/sites/5/2023/06/LB-tsport-freddie-copy.jpg?strip=all&quality=100&w=1500&h=1000&crop=1",
      "url" : "/videos/vid1.mp4",
      "no" : 10
    }
];

  const [currentPage, setCurrentPage] = useState(1);

  const handleNextPage = () => {
    setCurrentPage((prevPage) => prevPage + 1);
  };

  const handlePreviousPage = () => {
    setCurrentPage((prevPage) => prevPage - 1);
  };

  const startIndex = (currentPage - 1) * articlesPerPage;
  const endIndex = startIndex + articlesPerPage;
  const displayedArticles = articles.slice(startIndex, endIndex);

  const isPreviousDisabled = currentPage === 1;
  const isNextDisabled = endIndex >= articles.length;

  const VideoPage = () => {
    const location = useLocation();
    const { movieurl } = location.state;
    return (
      <div>
        <div className="video-container">
          <video controls className="responsive-video">
            <source src={movieurl} type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        </div>
      </div>
    );
  };

  return (
    <div>
      <Navbar />
      <br />
      <div className="container">
        <div className="container d-flex justify-content-between">
          <Router>
            <Switch>
              <Route path="/video-page">
                <div></div>
              </Route>
              <Route path="/">
                {!isPreviousDisabled && (
                  <button className="btn btn-dark" onClick={handlePreviousPage}>
                    Previous
                  </button>
                )}
                {!isNextDisabled && (
                  <button className="btn btn-dark" onClick={handleNextPage}>
                    Next
                  </button>
                )}
              </Route>
            </Switch>
          </Router>
        </div>
        <div className="row">
          <Router>
            <Switch>
              <Route path="/video-page" component={VideoPage} />
              <Route path="/">
                {displayedArticles.map((element) => (
                  <div className="col-md-4" key={element.no}>
                    <NavLink to="/video-page" state={{ movieurl: element.url }}>
                      <Content imageurl={element.img} movieurl={element.url} />
                    </NavLink>
                  </div>
                ))}
              </Route>
            </Switch>
          </Router>
        </div>
      </div>
    </div>
  );
}
