import React from "react";
import { Link } from "react-router-dom";
import { MDBIcon } from "mdb-react-ui-kit";

export default function Content(props) {
  const { imageurl, movieurl, index, total } = props;

  const handleNext = () => {
    // Logic to handle next button click
    // You can navigate to the next content item
  };

  const handlePrevious = () => {
    // Logic to handle previous button click
    // You can navigate to the previous content item
  };

  return (
    <div>
      <div className="container">
        <div className="row">
          <div className="items col-md">
            <div className="bg-image">
              <img src={imageurl} className="img-fluid" alt="Sample" />
              <div className="mask">
                <div className="d-flex justify-content-center align-items-center h-100">
                  <Link to={{ pathname: "/video-page", state: { movieurl } }}>
                    <p className="contenticon text-white mb-0">
                      <MDBIcon far icon="play-circle" />
                    </p>
                  </Link>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="row">
          <div className="col-md">
            <button
              className="btn btn-primary mr-2"
              onClick={handlePrevious}
              disabled={index === 0}
            >
              Previous
            </button>
            <button
              className="btn btn-primary"
              onClick={handleNext}
              disabled={index === total - 1}
            >
              Next
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
